import { useSelector } from "react-redux";
import Avatar from "react-avatar";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import { deleteContact } from "../redux/action/action";
import { useDispatch } from "react-redux";

import MovingText from 'react-moving-text'
import Snowfall from 'react-snowfall'
export default function Table() {
  const contacts = useSelector((state) => state.contact.contacts);
  console.log(contacts,"contact in redux");

  const dispatch = useDispatch();
  return (


    <>

<div style={{ position: 'relative' }} >
        <Snowfall style={{
          position: 'fixed',
          width: '100vw',
          height: '100vh'
        }} />
      </div>


    <div className="Contact container "  style={{marginTop:"100px"}}>



  <div className="row">
    <div className="col-md-10 mx-auto  sticky">

      <MovingText
        type="swing"
        duration="7s"
        delay="5s"
        direction="alternate-reverse"
        timing="ease"
        iteration="infinite"
        fillMode="both">

        <h1  className="col-md-6 mx-auto text-center text-light  ">Crud with redux </h1>
      </MovingText>
    </div>
  </div>
      <div style={{marginTop:"100px"}}>
      <table className="table text-light" >
        <thead>
          <tr>
            <th scope="col">id</th>
            <th scope="col">name</th>
            <th scope="col">username</th>
            <th scope="col">email</th>
            <th scope="col">edit</th>
            <th scope="col">Delete</th>
          </tr>
        </thead>
        <tbody>
          {contacts.map((contact) => (
            <tr key={contact.id}>
              <td>{contact.id}</td>
              <td>
                <Avatar
                  className="mr-2"
                  name={contact.name}
                  size="45"
                  round={true}
                />
                {contact.name}
              </td>
              <td>{contact.username}</td>
              <td>{contact.email}</td>

              <td className="creates">
              <Link to="/redux/AddR" className="text-primary font-weight-bold h4">create</Link>
            </td>

              <td>
                <Link to={`/redux/editR/${contact.id}` }>Edit</Link>
              </td>

              <td
                className="material-icons text-danger"
                onClick={() => dispatch(deleteContact(contact.id))}
                style={{cursor:"pointer"}}
              >
                Delete
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      </div>

    </div>

    </>
  );
}
